    const ERROR_MESSAGE = "This user has a lot of warnings, if you haven't seen the popup use the 'Force send!' button to mute, or warn regardless.";

    private MuteRepository $muteRepository;
    private WarningRepository $warningRepository;

    public function __construct(
        MuteRepository $muteRepository,
        WarningRepository $warningRepository,
    ) {
        $this->muteRepository = $muteRepository;
        $this->warningRepository = $warningRepository;
    }

    public function validate($value, Constraint $constraint): void
    {
        if (!isset($value->username) || trim($value->username) === '')
            return;

        if ($value->problematicOverride === true)
            return;

        $now = new \DateTimeImmutable('now', new \DateTimeZone('UTC'));
        $lastWeek = $now->modify('-1 week');
        $lastMonth = $now->modify('-1 month');

        $warningCountWeek = $this->warningRepository->countInPeriodForUsername($lastWeek, $now, $value->username);
        if ($warningCountWeek >= 3) {
            $this->addViolation($value->username);

            return;
        }

        $warningCountMonth = $this->warningRepository->countInPeriodForUsername($lastMonth, $now, $value->username);
        if ($warningCountMonth >= 5) {
            $this->addViolation($value->username);

            return;
        }


        $muteCountWeek = $this->muteRepository->countInPeriodForUsername($lastWeek, $now, $value->username);
        if ($muteCountWeek >= 3) {
            $this->addViolation($value->username);

            return;
        }

        $muteCountMonth = $this->muteRepository->countInPeriodForUsername($lastMonth, $now, $value->username);
        if ($muteCountMonth >= 5) {
            $this->addViolation($value->username);
        }

        if (in_array($value->username, self::PREVIOUS_DEACTIVATIONS))
            $this->addViolation($value->username);
    }

    private function addViolation(string $username): ?string
    {
        $this->context
            ->buildViolation(self::ERROR_MESSAGE)
            ->setCode($username)
            ->addViolation();

        return null;
    }
